package com.food.controller;import com.food.dao.model.Shop;import com.food.model.ShopModel;import com.food.service.ShopService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * Created by user on 16-12-3. */@Controller@RequestMapping(value = "/shop")public class ShopController {    private Logger logger = LoggerFactory.getLogger(ShopController.class);    @Autowired    private ShopService shopService;    @RequestMapping(value = "/put", method = RequestMethod.POST)    @ResponseBody    public String insert(@RequestBody ShopModel shopModel, HttpServletRequest httpServletRequest, HttpServletResponse httpServletResponse) {        if (shopModel == null || shopModel.getName() == null) {            return "fuck u";        }        Shop shop = Shop.getNewInstance(shopModel);        int count = shopService.insertWithoutRepeat(shop);        return count + "";    }}