package com.food.service;import com.food.dao.inter.IShopDao;import com.food.dao.model.Shop;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by user on 16-12-3. */@Servicepublic class ShopService {    @Autowired    IShopDao shopDao;    public int insert(Shop shop) {        return shopDao.insert(shop);    }    public List<Shop> selectShopByName(Shop shop) {        return shopDao.selectByShopName(shop);    }    public int insertWithoutRepeat(Shop shop) {        List<Shop> shopList = shopDao.selectByShopName(shop);        if (shopList != null && shopList.size() > 0) {            for (Shop exists : shopList) {                if (exists.compareTo(shop) == 0)                    return 0;            }        }        return shopDao.insert(shop);    }}