package com.food.service;import com.food.dao.inter.IMethodLogDao;import com.food.model.MethodLogModel;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Created by user on 16-11-10. */@Servicepublic class TestService {    @Autowired    IMethodLogDao methodLogDao;    public MethodLogModel TestFunction(int id) {        MethodLogModel methodLogModel = methodLogDao.selectOne(id);        return methodLogModel;    }}